{
  "code": "export default async function({ page, context }) { await page.goto('https://accounts.google.com/ServiceLogin?continue=https://aistudio.google.com/app/prompts/new_chat', {waitUntil: 'networkidle2'}); await page.waitForSelector('input[type=\"email\"]'); await page.type('input[type=\"email\"]', context.email); const screenshot = await page.screenshot({fullPage: true, encoding: 'base64'}); return {data: {screenshot: screenshot, status: 'email_entered'}, type: 'application/json'}; }",
  "context": {
    "email": "countescom@gmail.com"
  }
}
