// –ü–æ–ª—É—á–∞–µ–º HTML –∫–æ–Ω—Ç–µ–Ω—Ç
const html = $input.item.json;
const targetURL = $('‚öôÔ∏è Settings').item.json.targetURL;

let htmlContent = '';

// –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ä–∞–∑–Ω—ã–µ —Ñ–æ—Ä–º–∞—Ç—ã –æ—Ç–≤–µ—Ç–∞
if (typeof html === 'string') {
  htmlContent = html;
} else if (html && html.body) {
  htmlContent = html.body;
} else if (html && typeof html === 'object') {
  htmlContent = JSON.stringify(html);
} else {
  htmlContent = String(html);
}

// –†–µ–∑—É–ª—å—Ç–∞—Ç
const result = {
  success: false,
  websiteURL: targetURL,
  timestamp: new Date().toISOString(),
  captchaType: 'not_found',
  websiteKey: null,
  searchMethods: [],
  htmlLength: htmlContent.length
};

// –ú–µ—Ç–æ–¥ 1: data-sitekey attribute
try {
  const match1 = htmlContent.match(/data-sitekey\s*=\s*["']([0-9a-zA-Z_-]{10,100})["']/);
  if (match1 && match1[1]) {
    result.success = true;
    result.captchaType = 'standalone';
    result.websiteKey = match1[1];
    result.searchMethods.push('‚úÖ data-sitekey attribute');
    result.rucaptchaPayload = {
      clientKey: 'YOUR_RUCAPTCHA_API_KEY',
      task: {
        type: 'TurnstileTaskProxyless',
        websiteURL: targetURL,
        websiteKey: match1[1]
      }
    };
    return result;
  }
  result.searchMethods.push('‚ùå data-sitekey attribute - not found');
} catch (e) {
  result.searchMethods.push('‚ö†Ô∏è data-sitekey - error: ' + e.message);
}

// –ú–µ—Ç–æ–¥ 2: sitekey property –≤ JS –æ–±—ä–µ–∫—Ç–∞—Ö
try {
  const match2 = htmlContent.match(/["']sitekey["']\s*:\s*["']([0-9a-zA-Z_-]{10,100})["']/);
  if (match2 && match2[1]) {
    result.success = true;
    result.captchaType = 'standalone';
    result.websiteKey = match2[1];
    result.searchMethods.push('‚úÖ sitekey property');
    result.rucaptchaPayload = {
      clientKey: 'YOUR_RUCAPTCHA_API_KEY',
      task: {
        type: 'TurnstileTaskProxyless',
        websiteURL: targetURL,
        websiteKey: match2[1]
      }
    };
    return result;
  }
  result.searchMethods.push('‚ùå sitekey property - not found');
} catch (e) {
  result.searchMethods.push('‚ö†Ô∏è sitekey property - error: ' + e.message);
}

// –ú–µ—Ç–æ–¥ 3: iframe src —Å challenges.cloudflare.com
try {
  const match3 = htmlContent.match(/src\s*=\s*["']([^"']*challenges\.cloudflare\.com[^"']*)["']/);
  if (match3 && match3[1]) {
    const sitekeyFromIframe = match3[1].match(/[?&]sitekey=([^&"']+)/);
    if (sitekeyFromIframe && sitekeyFromIframe[1]) {
      result.success = true;
      result.captchaType = 'standalone_iframe';
      result.websiteKey = sitekeyFromIframe[1];
      result.searchMethods.push('‚úÖ iframe src');
      result.rucaptchaPayload = {
        clientKey: 'YOUR_RUCAPTCHA_API_KEY',
        task: {
          type: 'TurnstileTaskProxyless',
          websiteURL: targetURL,
          websiteKey: sitekeyFromIframe[1]
        }
      };
      return result;
    }
  }
  result.searchMethods.push('‚ùå iframe src - not found');
} catch (e) {
  result.searchMethods.push('‚ö†Ô∏è iframe src - error: ' + e.message);
}

// –ú–µ—Ç–æ–¥ 4: –ø–∞—Ç—Ç–µ—Ä–Ω 0x... (—Ö–∞—Ä–∞–∫—Ç–µ—Ä–Ω–æ –¥–ª—è Cloudflare)
try {
  const match4 = htmlContent.match(/["'](0x[0-9a-zA-Z_-]{10,100})["']/);
  if (match4 && match4[1]) {
    result.success = true;
    result.captchaType = 'html_extraction';
    result.websiteKey = match4[1];
    result.searchMethods.push('‚úÖ 0x pattern');
    result.rucaptchaPayload = {
      clientKey: 'YOUR_RUCAPTCHA_API_KEY',
      task: {
        type: 'TurnstileTaskProxyless',
        websiteURL: targetURL,
        websiteKey: match4[1]
      }
    };
    return result;
  }
  result.searchMethods.push('‚ùå 0x pattern - not found');
} catch (e) {
  result.searchMethods.push('‚ö†Ô∏è 0x pattern - error: ' + e.message);
}

// –ù–∏—á–µ–≥–æ –Ω–µ –Ω–∞–π–¥–µ–Ω–æ
result.error = 'Cloudflare Turnstile sitekey not found on page';
result.suggestion = 'Try increasing waitTime or check if the page actually uses Turnstile';

return result;
