const targetURL = $input.item.json.targetURL;
const waitTime = $input.item.json.waitTime;
const browserlessToken = $input.item.json.browserlessToken;

const functionCode = `module.exports = async ({ page, context }) => {
  const { targetURL, waitTime } = context;
  try {
    let capturedData = null;
    await page.evaluateOnNewDocument(() => {
      window.__turnstileData = null;
      Object.defineProperty(window, 'turnstile', {
        value: new Proxy({}, {
          get(target, prop) {
            if (prop === 'render') {
              return function(container, params) {
                console.log('[INTERCEPTED] Turnstile render');
                window.__turnstileData = {
                  sitekey: params.sitekey || params['site-key'],
                  action: params.action,
                  cData: params.cData || params['c-data'],
                  chlPageData: params.chlPageData || params['chl-page-data'],
                  theme: params.theme,
                  language: params.language,
                  size: params.size,
                  callback: typeof params.callback,
                  allParams: params
                };
                return 'intercepted-widget-id';
              };
            }
            return target[prop];
          }
        }),
        configurable: false
      });
    });
    console.log('[NAVIGATE] Going to ' + targetURL);
    await page.goto(targetURL, { waitUntil: 'networkidle2', timeout: 30000 });
    console.log('[WAIT] Waiting ' + waitTime + 'ms');
    await page.waitForTimeout(waitTime);
    capturedData = await page.evaluate(() => window.__turnstileData);
    if (!capturedData || !capturedData.sitekey) {
      console.log('[FALLBACK] Searching in page source');
      const pageContent = await page.content();
      const sitekeyMatch = pageContent.match(/sitekey[\\"':s]+(6[a-zA-Z0-9_-]{39,})/i);
      if (sitekeyMatch) {
        capturedData = { sitekey: sitekeyMatch[1], action: 'login', method: 'page_source_extraction', pageURL: targetURL };
      }
    }
    if (!capturedData || !capturedData.sitekey) {
      throw new Error('Could not extract Turnstile sitekey');
    }
    const result = {
      success: true,
      data: {
        cloudflare_turnstile: {
          sitekey: capturedData.sitekey,
          pageurl: targetURL,
          action: capturedData.action || 'login',
          cData: capturedData.cData,
          chlPageData: capturedData.chlPageData
        },
        raw_data: capturedData,
        rucaptcha_request: {
          method: 'turnstile',
          sitekey: capturedData.sitekey,
          pageurl: targetURL,
          data: capturedData.cData,
          pagedata: capturedData.chlPageData,
          action: capturedData.action || 'login',
          json: 1
        }
      },
      extracted_at: new Date().toISOString(),
      target_url: targetURL
    };
    console.log('[SUCCESS] Turnstile data extracted');
    return result;
  } catch (error) {
    console.error('[ERROR]', error.message);
    return { success: false, error: error.message, stack: error.stack, target_url: targetURL };
  }
};`;

const response = await fetch(`https://chrome.browserless.io/function?token=${browserlessToken}`, {
  method: 'POST',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({ code: functionCode, context: { targetURL: targetURL, waitTime: waitTime } })
});

const responseData = await response.json();
return { json: responseData };
