const https = require('https');

const requestBody = JSON.stringify({
  clientKey: '5fffba5de3bed2317417f625985ec684',
  task: {
    type: 'TurnstileTaskProxyless',
    websiteURL: 'https://rucaptcha.com/demo/cloudflare-turnstile',
    websiteKey: '0x4AAAAAAADnPIDROzbs0Qj'
  }
});

const options = {
  hostname: 'api.rucaptcha.com',
  port: 443,
  path: '/createTask',
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
    'Content-Length': Buffer.byteLength(requestBody)
  }
};

return new Promise((resolve, reject) => {
  const req = https.request(options, (res) => {
    let data = '';

    res.on('data', (chunk) => {
      data += chunk;
    });

    res.on('end', () => {
      try {
        const jsonData = JSON.parse(data);
        resolve([{json: jsonData}]);
      } catch (e) {
        resolve([{json: {error: 'Parse error', raw: data}}]);
      }
    });
  });

  req.on('error', (error) => {
    resolve([{json: {error: error.message}}]);
  });

  req.write(requestBody);
  req.end();
});
