// Создаем задачу через встроенный fetch
const requestBody = {
  clientKey: '5fffba5de3bed2317417f625985ec684',
  task: {
    type: 'TurnstileTaskProxyless',
    websiteURL: 'https://rucaptcha.com/demo/cloudflare-turnstile',
    websiteKey: '0x4AAAAAAADnPIDROzbs0Qj'
  }
};

const response = await fetch('https://api.rucaptcha.com/createTask', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json'
  },
  body: JSON.stringify(requestBody)
});

const data = await response.json();

return [{json: data}];
