const item = $input.first();

// Get image dimensions
const width = item.json.size?.width || item.json.width || 1536;
const height = item.json.size?.height || item.json.height || 1024;

// Calculate 40% dimensions
const newWidth = Math.round(width * 0.4);
const newHeight = Math.round(height * 0.4);

// Store original file size
const originalSize = item.binary?.data?.fileSize || 0;

return {
  json: {
    ...item.json,
    original_width: width,
    original_height: height,
    target_width: newWidth,
    target_height: newHeight,
    original_size_bytes: originalSize,
    original_size_mb: (originalSize / 1024 / 1024).toFixed(2)
  },
  binary: item.binary
};

const item = $input.first();

// Get processed file info
const processedSize = item.binary?.data?.fileSize || 0;
const processedWidth = item.json.size?.width || item.json.width || 0;
const processedHeight = item.json.size?.height || item.json.height || 0;

// Get original info
const originalSize = item.json.original_size_bytes || 0;
const originalWidth = item.json.original_width || 0;
const originalHeight = item.json.original_height || 0;

// Calculate compression
const compressionRatio = originalSize > 0 ? 
  ((1 - processedSize / originalSize) * 100).toFixed(1) : 0;

return {
  json: {
    ...item.json,
    result: {
      status: "completed",
      original: {
        dimensions: `${originalWidth}x${originalHeight}`,
        size: `${(originalSize / 1024 / 1024).toFixed(2)} MB`,
        size_kb: `${(originalSize / 1024).toFixed(1)} KB`
      },
      processed: {
        dimensions: `${processedWidth}x${processedHeight}`,
        size: `${(processedSize / 1024 / 1024).toFixed(2)} MB`,
        size_kb: `${(processedSize / 1024).toFixed(1)} KB`,
        format: item.binary?.data?.mimeType || 'unknown'
      },
      compression_ratio: `${compressionRatio}%`,
      resize_ratio: "40%"
    }
  },
  binary: item.binary
};
